import axios from 'axios'

export const URL = 'http://localhost:4000'
const serectId= process.env.REACT_APP_APPLICATION_ID || '0e76d9241dac9f9846e48298cfddff80460d2ae4'


const authHeaders = () => ({
  headers: {
    'Authorization': `serect ${serectId}`,
    'Content-Type': 'application/json'
  }
})

const headers = (accessToken) => ({
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json'
  }
})

const bookURL = `${URL}/v1/books`

const categoryURL = `${URL}/v1/categories`

export const fetchCategoryBook = (accessToken) =>
  axios.get(categoryURL, headers(accessToken))



